mapper.select:

 select b_no, mp_id, subject, content, ct_code, m_date, gender, capacity, regdt
from mateboard as TB
where b_no = #{b_no}

mapper.update:

update mateboard
set subject= #{subject}, content= #{content}, gender=#{gender}, ct_code=#{ct_code}, capacity=#{capacity}, m_date=#{m_date}
where b_no = #{b_no}
